const unzipper = require("unzipper");
const fs = require('fs');

var archive_path = [[UNZIP_PARAMETERS]][0];
var destination_folder = [[UNZIP_PARAMETERS]][1];

await (new Promise((resolve, reject) => {
    var stream = fs.createReadStream(archive_path);
    stream.on("error", function error(e) {
        reject(e);
    });
    var extractor = stream.pipe(unzipper.Extract({ path: destination_folder }));
    extractor.on("error", function error(e) {
        reject(e);
    });
    extractor.on("close", function close() {
        resolve();
    });
}));
