var self = this;

this.csv_generate = function(list, separator=","){
    var res = '';
    var first = true;
    list.forEach(function(item=""){
        var add = item;
        if(item.indexOf(";") > -1 || item.indexOf(":") > -1 || item.indexOf(",") > -1 || item.indexOf(separator) > -1){
            add = "\"" + add.replace(/["]/gi, "\"\"") + "\"";
        };
        if(!first){
            res += separator;
        }else{
            first = false;
        };
        res += add;
    });
    return res;
};
this.csv_parse = function(line){
    var res = [];
    var index = 0;
    var len = line.length;
    var InsideQuotes = false;
    var CurrentElement = '';

    while(index<len){
        var c = line[index];
        var n = '';
        var HasNext = (index + 1) < len;
        if(HasNext){
            n = line[index + 1];
        };
        index++;

        if(c=='\"'){
            if(InsideQuotes){
                if(!HasNext || n!='\"'){
                    InsideQuotes = false;
                    continue;
                }else{
                    CurrentElement += "\"";
                    index++;
                    continue;
                }
            }else{
                InsideQuotes = true;
                continue;
            }
        };

        if(c==':' || c==';' || c==','){
            if(InsideQuotes){
                CurrentElement += c;
                continue;
            }else{
                res.push(CurrentElement);
                CurrentElement = '';
                continue;
            }
        };
        CurrentElement += c;
    };
    res.push(CurrentElement);

    return res;
};
this.replace_null = function(rows){
    return rows.map(function(row){return row.map(function(cell){return (cell==null) ? "" : cell})})
};
this.format_data = function(data){
    if(typeof data!=="object"){
        data = data.split(/\r?\n/);
    };
    if(typeof data[0]!=="object"){
        data = data.map(function(row){return (typeof row!=="object") ? self.csv_parse(row) : row});
    };
    return data;
};
this.format_results = function(results, format){
    results = self.replace_null(results);
    if(format=="CSV list" || format=="CSV string"){
        results = results.map(function(row){return self.csv_generate(row, ":")});
        if(format=="CSV string"){
            results = results.join("\r\n");
        };
    };
    return results;
};
this.get_or_create_sheet = function(workbook, sheet_index_or_name, exist){
    var sheet = workbook.sheet(sheet_index_or_name);
    if(!sheet){
        var sheet_name = "";
        var sheet_index = "";
        if(typeof sheet_index_or_name=="number"){
            sheet_name = "NewSheet" + Math.floor(Math.random() * 100) + 1;
            sheet_index = sheet_index_or_name;
        }else{
            sheet_name = sheet_index_or_name;
            sheet_index = null;
        };
        sheet = exist ? workbook.addSheet(sheet_name, sheet_index) : (sheet_index ? workbook.addSheet(sheet_name, sheet_index) : workbook.sheet(0).name(sheet_name));
    };
    return sheet;
};
