const Imap = require('imap');
const { format } = require('util');
const { Buffer } = require('buffer');
const { createHash } = require('crypto');
const { once, EventEmitter } = require('events');
const qp = require('quoted-printable');
const iconv = require('iconv-lite');
const utf8 = require('utf8');
const uuencode = require('uuencode');

class ImapClient extends EventEmitter {
    constructor(id, { config, box='INBOX', timeout=300000, debug=false }, logger=console.log){
        super();
        this.locked = true;
        this.id = id;
        this.config = config;
        this.box = box;
        this.timeout = 0;
        this.timeoutMs = timeout;
        this.debug = debug;
        this.logger = logger;

        let opts = {
            user: config.username,
            password: config.password,
            host: config.host,
            port: config.port
        };

        if(['ssl', 'tls'].includes(config.encrypt)){
            opts.tls = true;
            opts.tlsOptions = {
                servername: config.host
            };
        }else if(config.encrypt === 'starttls'){
            opts.tls = false;
            opts.autotls = 'always';
        };

        if(debug){
            opts.debug = this.log;
        };

        this.imap = new Imap(opts);
    }

    static async init(opts, logger){
        let { config, box='INBOX', timeout, debug } = opts;
        let id = createHash('md5').update(`${config.host}@${config.username}:${config.password}`).digest('hex');

        if(typeof _imap_connections!="undefined" && typeof _imap_connections[id]!="undefined" && _imap_connections[id].locked){
            await once(_imap_connections[id], 'unlock');

            return await this.init(opts, logger);
        };

        if(typeof _imap_connections=="undefined" || typeof _imap_connections[id]=="undefined" || JSON.stringify(_imap_connections[id].config)!=JSON.stringify(config)){

            if(typeof _imap_connections=="undefined"){
                global._imap_connections = {};
            };

            _imap_connections[id] = new this(id, opts, logger);

            await _imap_connections[id].connect();
        }else{

            _imap_connections[id].clearTimeout();

            if(typeof _imap_connections=="undefined" || typeof _imap_connections[id]=="undefined"){
                return await this.init(opts, logger);
            };

            _imap_connections[id].setTimeoutMs(timeout);
            _imap_connections[id].setBox(box);
            _imap_connections[id].setDebug(debug, logger);
        };

        return _imap_connections[id];
    }

    lock = () => {
        this.locked = true;
        this.emit('lock');
    }

    unlock = () => {
        this.locked = false;
        this.emit('unlock');
    }

    del = () => {
        if(typeof _imap_connections!="undefined" && typeof _imap_connections[this.id]!="undefined"){
            return delete _imap_connections[this.id];
        };

        return true;
    }

    close = () => {
        this.clearTimeout();
        this.imap.removeAllListeners('close');
        if(this.imap.state && this.imap.state !== 'disconnected'){
            try{
                this.imap.end();
            }catch(_){}
        };
        this.unlock();
        this.closed = true;
        this.emit('close');
        return this.del();
    }

    setTimeout = (ms=this.timeoutMs) => {
        this.clearTimeout();
        if(!this.closed){
            this.timeout = setTimeout(this.close, ms);
        };
    }

    clearTimeout = () => {
        if(this.timeout && !this.timeout["_destroyed"]){
            clearTimeout(this.timeout);
        };
    }

    connect = () => {
        return new Promise((resolve, reject) => {
            let imapOnReady = () => {
                this.imap.removeListener('error', imapOnError);
                this.imap.removeListener('close', imapOnClose);
                this.imap.removeListener('end', imapOnEnd);
                this.imap.once('close', this.close);
                this.unlock();
                resolve(this);
            }

            let imapOnError = (err) => {
                this.imap.removeListener('ready', imapOnReady);
                this.imap.removeListener('close', imapOnClose);
                this.imap.removeListener('end', imapOnEnd);
                this.close();
                reject(err);
            }

            let imapOnEnd = () => {
                this.imap.removeListener('ready', imapOnReady);
                this.imap.removeListener('error', imapOnError);
                this.imap.removeListener('close', imapOnClose);
                this.close();
                reject(new Error('Connection ended unexpectedly'));
            }

            let imapOnClose = () => {
                this.imap.removeListener('ready', imapOnReady);
                this.imap.removeListener('error', imapOnError);
                this.imap.removeListener('end', imapOnEnd);
                this.close();
                reject(new Error('Connection closed unexpectedly'));
            }

            this.imap.once('ready', imapOnReady);
            this.imap.once('error', imapOnError);
            this.imap.once('close', imapOnClose);
            this.imap.once('end', imapOnEnd);

            this.imap.connect();
        });
    }

    setTimeoutMs = (ms) => {
        if(ms && this.timeoutMs !== ms){
            this.timeoutMs = ms;
        };
    }

    setBox = (box) => {
        if(box && this.box !== box){
            this.box = box;
        };
    }

    setDebug = (enable, logger) => {
        if(typeof enable !== "undefined" && this.debug !== enable){
            this.debug = enable;
            if(enable){
                this.imap.debug = this.log;
                if(this.imap._parser){
                    this.imap._parser.debug = this.log;
                };
            }else{
                this.imap.debug = undefined;
                if(this.imap._parser){
                    this.imap._parser.debug = undefined;
                };
            };
        };
        this.logger = logger;
    }

    log = (...args) => {
        if(this.debug){
            this.logger(format(...args));
        };
    }

    openBox = async (name = this.box, readOnly = false) => {
        if(this.imap._box && this.imap._box.name === name){
            return this.imap._box;
        }else{
            return await (new Promise((resolve, reject) => {
                this.imap.openBox(name, readOnly, function(err, box){
                    if(err){
                        reject(err);
                    }else{
                        resolve(box);
                    };
                });
            }));
        };
    }

    closeBox = async (autoExpunge = true) => {
        if(typeof this.imap._box === 'undefined'){
            return;
        }else{
            return await (new Promise((resolve, reject) => {
                this.imap.closeBox(autoExpunge, function(err){
                    if(err){
                        reject(err);
                    }else{
                        resolve();
                    };
                });
            }));
        };
    }

    addBox = (name) => {
        return new Promise((resolve, reject) => {
            this.imap.addBox(name, function(err){
                if(err){
                    reject(err);
                }else{
                    resolve();
                };
            });
        });
    }

    delBox = (name) => {
        return new Promise((resolve, reject) => {
            this.imap.delBox(name, function(err){
                if(err){
                    reject(err);
                }else{
                    resolve();
                };
            });
        });
    }

    renameBox = (oldname, newname) => {
        return new Promise((resolve, reject) => {
            this.imap.renameBox(oldname, newname, function(err){
                if(err){
                    reject(err);
                }else{
                    resolve();
                };
            });
        });
    }

    status = async (name = this.box) => {
        if(this.imap._box && this.imap._box.name === name){
            await this.closeBox(false);
        };
        return await (new Promise((resolve, reject) => {
            this.imap.status(name, function(err, box){
                if(err){
                    reject(err);
                }else{
                    resolve(box);
                };
            });
        }));
    }

    getBoxes = (namespace = '') => {
        return new Promise((resolve, reject) => {
            this.imap.getBoxes(namespace, function(err, boxes){
                if(err){
                    reject(err);
                }else{
                    resolve(boxes);
                };
            });
        });
    }

    expunge = (uids) => {
        return new Promise((resolve, reject) => {
            this.imap.expunge(uids, function(err){
                if(err){
                    reject(err);
                }else{
                    resolve();
                };
            });
        });
    }

    search = (criteria) => {
        return new Promise((resolve, reject) => {
            this.imap.search(criteria, function(err, uids){
                if(err){
                    reject(err);
                }else{
                    resolve(uids);
                };
            });
        });
    }

    esearch = (criteria, options) => {
        return new Promise((resolve, reject) => {
            this.imap.esearch(criteria, options, function(err, info){
                if(err){
                    reject(err);
                }else{
                    resolve(info);
                };
            });
        });
    }

    sort = (sorts, criteria) => {
        return new Promise((resolve, reject) => {
            this.imap.sort(sorts, criteria, function(err, uids){
                if(err){
                    reject(err);
                }else{
                    resolve(uids);
                };
            });
        });
    }

    getFlags = (uids) => {
        return new Promise((resolve, reject) => {
            let messages = [];
            if(!Array.isArray(uids)){
                uids = [uids];
            };
            uids = uids.join(',');
            this.imap._enqueue('UID FETCH ' + uids + ' (FLAGS)');
            let req = this.imap._queue[this.imap._queue.length - 1];
            req.fetchCache = {};
            req.fetching = ['FLAGS'];
            let fetch = req.bodyEmitter = new EventEmitter();
            function fetchOnMessage(msg){
                let flags = [];
                function messageOnAttributes(attrs){
                    if(attrs.flags){
                        flags.push(...attrs.flags);
                    };
                };
                msg.once('attributes', messageOnAttributes);
                msg.once('end', function(){
                    msg.removeListener('attributes', messageOnAttributes);
                    messages.push(flags);
                });
            };
            function fetchOnError(err){
                fetch.removeListener('message', fetchOnMessage);
                fetch.removeListener('end', fetchOnEnd);
                reject(err);
            };
            function fetchOnEnd(){
                fetch.removeListener('message', fetchOnMessage);
                fetch.removeListener('error', fetchOnError);
                resolve(messages);
            };
            fetch.on('message', fetchOnMessage);
            fetch.once('error', fetchOnError);
            fetch.once('end', fetchOnEnd);
        });
    }

    addFlags = (uids, flags) => {
        return new Promise((resolve, reject) => {
            this.imap.addFlags(uids, flags, function(err){
                if(err){
                    reject(err);
                }else{
                    resolve();
                };
            });
        });
    }

    delFlags = (uids, flags) => {
        return new Promise((resolve, reject) => {
            this.imap.delFlags(uids, flags, function(err){
                if(err){
                    reject(err);
                }else{
                    resolve();
                };
            });
        });
    }

    setFlags = (uids, flags) => {
        return new Promise((resolve, reject) => {
            this.imap.setFlags(uids, flags, function(err){
                if(err){
                    reject(err);
                }else{
                    resolve();
                };
            });
        });
    }

    addKeywords = (uids, keywords) => {
        return new Promise((resolve, reject) => {
            this.imap.addKeywords(uids, keywords, function(err){
                if(err){
                    reject(err);
                }else{
                    resolve();
                };
            });
        });
    }

    delKeywords = (uids, keywords) => {
        return new Promise((resolve, reject) => {
            this.imap.delKeywords(uids, keywords, function(err){
                if(err){
                    reject(err);
                }else{
                    resolve();
                };
            });
        });
    }

    setKeywords = (uids, keywords) => {
        return new Promise((resolve, reject) => {
            this.imap.setKeywords(uids, keywords, function(err){
                if(err){
                    reject(err);
                }else{
                    resolve();
                };
            });
        });
    }

    copy = (uids, toBox) => {
        return new Promise((resolve, reject) => {
            this.imap.copy(uids, toBox, function(err){
                if(err){
                    reject(err);
                }else{
                    resolve();
                };
            });
        });
    }

    move = (uids, toBox) => {
        return new Promise((resolve, reject) => {
            this.imap.move(uids, toBox, function(err){
                if(err){
                    reject(err);
                }else{
                    resolve();
                };
            });
        });
    }

    fetch = (uids, options) => {
        const self = this;
        return new Promise((resolve, reject) => {
            let fetch = self.imap.fetch(uids, options);
            let messages = [];
            let isHeader = /^HEADER/g;
            function fetchOnMessage(msg, seqno){
                self.getMessage(msg).then(function(message){
                    message.seqno = seqno;
                    messages.push(message);
                });
            };
            function fetchOnError(err){
                fetch.removeListener('message', fetchOnMessage);
                fetch.removeListener('end', fetchOnEnd);
                reject(err);
            };
            function fetchOnEnd(){
                fetch.removeListener('message', fetchOnMessage);
                fetch.removeListener('error', fetchOnError);
                resolve(messages);
            };
            fetch.on('message', fetchOnMessage);
            fetch.once('error', fetchOnError);
            fetch.once('end', fetchOnEnd);
        });
    }

    serverSupports = (cap) => {
        return this.imap.serverSupports(cap);
    }

    getMessage = (msg) => {
        return new Promise(function (resolve){
            let message = {
                attributes: undefined,
                parts: []
            };
            function messageOnBody(stream, info){
                let body = '';
                function streamOnData(chunk){
                    body += chunk.toString('utf8');
                };
                stream.on('data', streamOnData);
                stream.once('end', function(){
                    stream.removeListener('data', streamOnData);
                    message.parts.push({
                        which: info.which,
                        size: info.size,
                        body: body
                    });
                });
            };
            function messageOnAttributes(attrs){
                message.attributes = attrs;
            };
            function messageOnEnd(){
                msg.removeListener('body', messageOnBody);
                msg.removeListener('attributes', messageOnAttributes);
                resolve(message);
            };
            msg.on('body', messageOnBody);
            msg.once('attributes', messageOnAttributes);
            msg.once('end', messageOnEnd);
        });
    }

    decodeWords = (str) => {
        return this.parseHeader(`val: ${str}`).val[0];
    }

    getParts = (struct, parts = []) => {
        for(let part of struct){
            if(Array.isArray(part)){
                this.getParts(part, parts);
            }else if(part.partID){
                if(part.params && part.params.name){
                    part.params.name = this.decodeWords(part.params.name);
                };
                if(part.disposition && part.disposition.params && part.disposition.params.filename){
                    part.disposition.params.filename = this.decodeWords(part.disposition.params.filename);
                };
                parts.push(part);
            };
        };
        return parts;
    }

    getPartData = (uid, part) => {
        const self = this;
        return new Promise(function (resolve, reject){
            let fetch = self.imap.fetch(uid, {bodies: [part.partID]});
            function fetchOnMessage(msg){
                self.getMessage(msg).then(function(result){
                    if(result.parts.length !== 1){
                        reject(new Error('Got ' + result.parts.length + ' parts, should get 1'));
                        return;
                    };
                    let data = result.parts[0].body;
                    let encoding = part.encoding.toLocaleLowerCase();
                    let charset = (part.params && part.params.charset) ? part.params.charset.toLocaleLowerCase() : '';
                    if(encoding === 'base64'){
                        resolve(Buffer.concat(data.split(/\r?\n/).map(function(b){return Buffer.from(b, 'base64')})));
                    }else if(encoding === 'quoted-printable'){
                        if(charset === 'utf-8'){
                            resolve(Buffer.from(utf8.decode(qp.decode(data))).toString());
                        }else{
                            resolve(Buffer.from(qp.decode(data)).toString());
                        };
                    }else if(encoding === '7bit' || encoding === '7bits'){
                        resolve(Buffer.from(data).toString('ascii'));
                    }else if(encoding === '8bit' || encoding === '8bits' || encoding === 'binary'){
                        resolve(iconv.decode(Buffer.from(data), charset || 'utf-8'));
                    }else if(encoding === 'uuencode'){
                        let parts = data.toString().split('\n');
                        let merged = parts.splice(1, parts.length - 4).join('');
                        resolve(uuencode.decode(merged));
                    }else{
                        reject(new Error('Unknown encoding ' + part.encoding));
                    };
                });
            };
            function fetchOnError(err){
                fetch.removeListener('message', fetchOnMessage);
                fetch.removeListener('end', fetchOnEnd);
                reject(err);
            };
            function fetchOnEnd(){
                fetch.removeListener('message', fetchOnMessage);
                fetch.removeListener('error', fetchOnError);
            };
            fetch.once('message', fetchOnMessage);
            fetch.once('error', fetchOnError);
            fetch.once('end', fetchOnEnd);
        });
    }

    delParent = (boxes) => {
        for(let name in boxes){
            let box = boxes[name];
            delete box.parent;
            if(box.children){
                this.delParent(box.children);
            };
        };
        return boxes;
    }

    randStr = (length = 10, chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789') => {
        let str  = '';
        for(var i = 0; i < length; i++){
            str += chars.charAt(Math.floor(Math.random() * chars.length));
        };
        return str;
    }

    getParamInfo = (data = []) => {
        if(!data){
            data = [];
        };
        if(!Array.isArray(data)){
            data = [data];
        };
        let raw = false;
        data = data.filter(function(d){return !(raw = (d === 'raw'))});
        let base = data.length > 0;
        return {
            base,
            raw,
            any: base || raw,
            data
        };
    }

    parseHeader = Imap.parseHeader
}

