const ImapClient = require("../InMail_ImapClient");
const { writeFile } = require('fs').promises;
const { resolve } = require('path');

let { uids, body, headers, size, attachnames, attachments, markSeen, box, connect } = [[INMAIL_NODE_PARAMETERS]];

let client = await ImapClient.init(connect, console.log);
try{
    await client.openBox(box);
    
    let options = {
        markSeen,
        size
    };

    body = client.getParamInfo(body);
    headers = client.getParamInfo(headers);

    if(headers.any || body.raw){
        options.bodies = [];
    };

    if(headers.base){
        options.bodies.push(`HEADER.FIELDS (${headers.data.map(function(h){return h.toLocaleUpperCase()}).join(' ')})`);
    };

    if(headers.raw){
        options.bodies.push('HEADER');
    };

    if(body.raw){
        options.bodies.push('TEXT');
    };

    if(body.base || attachments){
        options.struct = true;
    };

    let msgs = await client.fetch(uids, options);
    
    let messages = [];

    for(let msg of msgs){
        let attrs = msg.attributes;
        let parts = msg.parts;
        let message = {};
        if(body.any){
            message.body = {};
        };
        if(headers.any){
            message.headers = {};
            if(headers.raw){
                message.headers.raw = {};
            };
        };
        messages.push(message);
        for(let key in attrs){
            if(key !== 'struct'){
                message[key] = key === 'date' ? attrs[key].toString() : attrs[key];
            };
        };
        for(let part of parts){
            if(part.which.startsWith('HEADER')){
                let parsed = client.parseHeader(part.body);
                let obj = part.which === 'HEADER' ? message.headers.raw : message.headers;
                for(let key in parsed){
                    let value = parsed[key];
                    obj[key] = Array.isArray(value) && value.length < 2 ? value[0] : value;
                };
            }else{
                message.body.raw = part.body;
            };
        };
        if(body.base || attachments){
            parts = client.getParts(attrs.struct);
            if(body.base){
                for(let type of body.data){
                    let part = parts.filter(function(p){return p.type.toLocaleLowerCase() === 'text' && p.subtype.toLocaleLowerCase() === type})[0] || null;
                    if(part){
                        message.body[type] = (await client.getPartData(attrs.uid, part)).toString();
                    }else{
                        message.body[type] = '';
                    };
                };
            };
            if(attachments){
                message.attachments = [];
                let filtered = parts.filter(function(p){return p.disposition && p.disposition.type.toLocaleLowerCase() === 'attachment'});
                if(attachnames){
                    message.attachnames = filtered.map(function(p){return p.disposition.params.filename});
                };
                if(filtered.length && attachments !== true && attachments.toString().trim() !== '*'){
                    if(!Array.isArray(attachments)){
                        attachments = attachments.split(';');
                    };
                    let maskFiltered = [];
                    let ids = [];
                    for(let mask of attachments){
                        let regex = new RegExp(mask.replace(/\*([^*])*?/gi, '.*'));
                        for(let part of filtered){
                            if(regex.test(part.disposition.params.filename) && !ids.includes(part.partID)){
                                maskFiltered.push(part);
                                ids.push(part.partID);
                            };
                        };
                    };
                    filtered = maskFiltered;
                };
                if(filtered.length){
                    for(let part of filtered){
                        let filename = part.disposition.params.filename;
                        let randomFile = resolve(`${client.randStr()}.file`).split('\\').join('/');
                        await writeFile(randomFile, (await client.getPartData(attrs.uid, part)));
                        message.attachments.push({
                            name: filename,
                            type: `${part.type}/${part.subtype}`,
                            path: randomFile
                        });
                    };
                };
            };
        };
    };

    [[INMAIL_NODE_PARAMETERS]] = messages;
}catch(err){
    client.setTimeout();
    throw err;
};
