const Sequelize = require('sequelize');
const SqlString = require('sequelize/lib/sql-string');
var self = this;

this.connect_sql = async function(config){
    var {id, data, connect_timeout, debug} = config;
    var dialect = data.dialect;

    if(typeof sql_connections=="undefined"){
        sql_connections = {};
    };

    if(typeof sql_connections[id]=="undefined"){
        sql_connections[id] = {};
    };

    if(typeof sql_connections[id][dialect]=="undefined" || JSON.stringify(sql_connections[id][dialect].config)!=JSON.stringify(data)){
        var options = {};
        var keys = Object.keys(data);
        for(var key of keys){
            if(data[key]!=="" && key!="storage"){
                options[key] = data[key];
            };
        };
        if(dialect==="sqlite"){
            if(data.storage){
                options.storage = data.storage;
            };
            options.dialectModule = require('sqlite3-offline').verbose();
        };
        if(dialect!=="sqlite" && typeof connect_timeout==='number' && connect_timeout > -1){
            switch(dialect){
                case 'mysql':
                case 'mariadb':
                    options.dialectOptions = {connectTimeout: connect_timeout};
                    break;
                case 'mssql':
                    options.dialectOptions = {options: {connectTimeout: connect_timeout} };
                case 'postgres':
                    options.dialectOptions = {connectionTimeoutMillis: connect_timeout};
            };
        };
        if(debug){
            options.logging = BAS_CONSOLE_LOG;
        };
        options.define = {freezeTableName: true};
        sql_connections[id][dialect] = {
            id: id,
            dialect: dialect,
            config: data,
            sequelize: new Sequelize(options),
            timeout: 0,
            close: function(id=this.id, dialect=this.dialect){
                if(typeof sql_connections!="undefined" && typeof sql_connections[id]!="undefined" && typeof sql_connections[id][dialect]!="undefined"){
                    var sql = sql_connections[id][dialect];
                    sql.clearTimeout();
                    sql.sequelize.close();
                    return sql.delete();
                };
            },
            setTimeout: function(timeout){
                this.clearTimeout();
                this.timeout = setTimeout(this.close, timeout, this.id, this.dialect);
            },
            clearTimeout: function(){
                if(!this.timeout["_destroyed"]){
                    clearTimeout(this.timeout);
                };
            },
            delete: function(){
                delete sql_connections[this.id][this.dialect];
                if(Object.keys(sql_connections[this.id]).length==0){
                    delete sql_connections[this.id];
                };
            }
        };
        var sql_obj = sql_connections[id][dialect];
        try{
            await sql_obj.sequelize.authenticate();
            return sql_obj;
        }catch(err){
            sql_obj.delete();
            throw err;
        };
    }else{
        sql_connections[id][dialect].clearTimeout();
        if(typeof sql_connections=="undefined" || typeof sql_connections[id]=="undefined" || typeof sql_connections[id][dialect]=="undefined"){
            var sql_obj = await self.connect_sql(config);
            return sql_obj;
        }else{
            return sql_connections[id][dialect];
        };
    };
};
this.get_model = async function(sql, table, debug){
    var sequelize = sql.sequelize;
    var models = sequelize.models;
    if(Object.keys(models).includes(table)){
        return models[table];
    }else{
        var QueryInterface = sequelize.getQueryInterface();
        var schema = await QueryInterface.describeTable(table);
        if(debug){
            BAS_CONSOLE_LOG("Result: " + JSON.stringify(schema));
        };
        sql.fields = Object.keys(schema);
        var primary_key = false;
        for(var key of sql.fields){
            var field = schema[key];
            if(field["primaryKey"]){
                primary_key = true;
                if(sql.dialect==='sqlite' && field["type"] && field["type"].toLocaleUpperCase()===("INTEGER")){
                    let result = await sequelize.query(`SELECT 1 FROM sqlite_master WHERE type = 'table' AND name = '${table}' AND sql LIKE '%AUTOINCREMENT%'`, {type:"SELECT"});
                    if(debug){
                        BAS_CONSOLE_LOG("Result: " + JSON.stringify(result));
                    };
                    field["autoIncrement"] = result.length > 0;
                };
            };
            if((field["allowNull"]===false || field["autoIncrement"]===true) && field["defaultValue"]===null){
                if(field["autoIncrement"]===true){
                    delete field["defaultValue"];
                }else{
                    field["defaultValue"] = false;
                };
            };
        };
        if(!primary_key){
            schema[sql.fields[0]]["primaryKey"] = true;
        };
        var model = sequelize.define(table, schema, {timestamps: false});
        if(debug){
            BAS_CONSOLE_LOG("Result: " + JSON.stringify(schema));
        };
        return model;
    };
};
this.format = function(sql, replacements, dialect){
    return SqlString.format(sql, replacements, null, dialect);
};
this.restore_dates = function(values){
    values = [].concat(values);
    return values.map(function(value){return (typeof value=="object" && value!==null && value.isDate) ? new Date(value.date) : (Array.isArray(value) ? value.map(function(e){return (typeof e=="object" && e!==null && e.isDate) ? new Date(e.date) : e}) : value)});
};
this.restore_dates_in_object = function(obj, convert = true){
    var new_obj = {};
    for(const key in obj){
        var value = obj[key];
        new_obj[key] = (typeof value=="object" && value!==null && value.isDate) ? new Date(value.date) : (Array.isArray(value) ? value.map(function(e){return (typeof e=="object" && e!==null && e.isDate) ? new Date(e.date) : (convert ? self.convert(e) : e)}) : (convert ? self.convert(value) : value));
    };
    return new_obj;
};
this.csv_generate = function(list, separator=":"){
    var res = '';
    var first = true;
    list.forEach(function(item=""){
        var add = item;
        if(typeof item=="string" && (item.includes(";") || item.includes(":") || item.includes(",") || item.includes(separator))){
            add = "\"" + add.replace(/["]/gi, "\"\"") + "\"";
        };
        if(item instanceof Date){
            add = "\"" + add.toString() + "\"";
        };
        if(!first){
            res += separator;
        }else{
            first = false;
        };
        res += add;
    });
    return res;
};
this.csv_parse = function(line, convert = true){
    var res = [];
    var index = 0;
    var len = line.length;
    var InsideQuotes = false;
    var CurrentElement = '';

    while(index<len){
        var c = line[index];
        var n = '';
        var HasNext = (index + 1) < len;
        if(HasNext){
            n = line[index + 1];
        };
        index++;

        if(c=='\"'){
            if(InsideQuotes){
                if(!HasNext || n!='\"'){
                    InsideQuotes = false;
                    continue;
                }else{
                    CurrentElement += "\"";
                    index++;
                    continue;
                }
            }else{
                InsideQuotes = true;
                continue;
            }
        };

        if(c==':' || c==';' || c==','){
            if(InsideQuotes){
                CurrentElement += c;
                continue;
            }else{
                res.push(convert ? self.convert(CurrentElement) : CurrentElement);
                CurrentElement = '';
                continue;
            }
        };
        CurrentElement += c;
    };
    res.push(convert ? self.convert(CurrentElement) : CurrentElement);

    return res;
};
this.format_results = function(results, format){
    if(format=="Object list" || format=="2D list"){
        results.forEach(function(row, ir){Object.keys(row).forEach(function(key){results[ir][key] = (row[key] instanceof Date) ? ({isDate:true,date:row[key].toString()}) : row[key]})});
    };
    if(format=="2D list" || format=="CSV list" || format=="CSV string"){
		results = results.map(function(row){return Object.values(row)});
        if(format=="CSV list" || format=="CSV string"){
            results = results.map(function(row){return self.csv_generate(row, ":")});
            if(format=="CSV string"){
                results = results.join("\n");
            };
        };
    };
    return results;
};
this.convert = function(data){
    return (typeof data=="string" && data!=="") ? (isNaN(data) ? (data=="true" || data=="false" ? data=="true" : (data==="null") ? null : data) : Number(data)) : data;
};
this.format_data = function(data, fields, convert = true){
    if(typeof data=="string"){
        var reg = /\r?\n/;
        data = data.split(reg);
    };
    if(typeof data=="object" && ((typeof data[0]=="object" && Array.isArray(data[0])) || typeof data[0]!="object")){
        data = data.map(function(row){return (typeof row!="object") ? self.csv_parse(row, convert) : (convert ? row.map(function(cell){return self.convert(cell)}) : row)});
        data = data.map(function(row){
			var fields_cut = fields.slice(0, row.length);
			return Object.assign(...fields_cut.map(function(field, i){return {[field]:row[i]}}));
		});
    };
    if(typeof data=="object" && (typeof data[0]=="object" && !Array.isArray(data[0]))){
        data = data.map(function(row){return self.restore_dates_in_object(row, convert)});
    };
    return data;
};

