var [config, query, query_type, data_format] = [[SQL_NODE_PARAMETERS]];
var {id, timeout, debug} = config;
var [query_sql, query_parameterize, query_replacements] = query;

var options = {type:query_type};
if(query_parameterize){
    options.replacements = util.restore_dates(query_replacements);
};
if(debug){
    options.logging = BAS_CONSOLE_LOG;
};

var sql = await util.connect_sql(config);
try{
    var sequelize = sql.sequelize;
    var results = await sequelize.query(query_sql, options);
    if(debug){
        BAS_CONSOLE_LOG("Result: " + JSON.stringify(results));
    };
    if(query_type=="RAW"){
        results = (results[0]==null || typeof results[0]=="undefined") ? "" : results[0];
    };
    [[SQL_NODE_PARAMETERS]] = query_type=="SELECT" ? [util.format_results(results, data_format), data_format, query_type] : [results, data_format, query_type];
    await sequelize.sync();
    sql.setTimeout(timeout);
}catch(err){
    sql.setTimeout(timeout);
    throw err;
};

var id = [[SQL_NODE_PARAMETERS]];

if(typeof sql_connections!="undefined" && typeof sql_connections[id]!="undefined"){
    var dialects = Object.keys(sql_connections[id]);
    for(var dialect of dialects){
        sql_connections[id][dialect].close();
    };
};

const util = require("../UtilSQL");

var [config, table, where, included_columns, excluded_columns, order, offset, limit, data_format] = [[SQL_NODE_PARAMETERS]];
var {id, timeout, debug} = config;
var [where, where_parameterize, where_replacements] = where;

if(where_parameterize){
    replacements = util.restore_dates(where_replacements);
    where = util.format(where, replacements, config.dialect);
};

var sql = await util.connect_sql(config);
try{
    var sequelize = sql.sequelize;
    var model = await util.get_model(sql, table, debug);
    var options = {};
    if(debug){
        options.logging = BAS_CONSOLE_LOG;
    };
    if(where!==""){
        if(typeof where=="object"){
            options.where = where;
        }else{
            options.where = sequelize.literal(where);
        };
    };
    if(included_columns!=="" && excluded_columns==""){
        options.attributes = included_columns;
    };
    if(excluded_columns!==""){
        options.attributes = {exclude:excluded_columns};
    };
    if(order!==""){
        options.order = order;
    };
    if(offset!==""){
        options.offset = offset;
    };
    if(limit!==""){
        options.limit = limit;
    };
    var results = await model.findAll(options);
    results = results.map(function(row){return row.get()});
    if(debug){
        BAS_CONSOLE_LOG("Result: " + JSON.stringify(results));
    };
    [[SQL_NODE_PARAMETERS]] = [util.format_results(results, data_format), data_format];
    sql.setTimeout(timeout);
}catch(err){
    sql.setTimeout(timeout);
    throw err;
};

